INSERT INTO `crm_request_manage` VALUES (1168, 2021, 2, '运营部', 'java后台', 1865, '蒋明', '分享有礼老数据订单优化', '需求背景：现查询到较久远的订单，无法上传截图参与活动，原因是订单较久远，部分订单数据没有同步至订单表中，因此不能参与活动。\n功能规划：为满足较久远订单的用户也能参与分享有礼活动，现出方案多个订单表联查，进行两边订单表的查询。以达到该该目的。', 2, '2021-02-04', NULL, '2021-02-05', '2021-02-05', '2021-02-04 15:10:53', 1, 3, NULL, NULL);
