INSERT INTO `crm_request_manage` VALUES (1208, 2021, 3, '研发部', 'java', 1413, '黄磊', '返回图片链接给到移动端（正式课和试听课）', '后台需要返回图片链接给到移动端，正式课和试听课是两张不同的图片，图片上带有二维码（具体见设计稿），识别二维码打开的是H5链接（正式课和试听课是两个不同的链接）\n说明：这个任务是为了配合老师app任务-TEACHER-1013，分享课程评价到企微的任务 ', 1, '2021-02-23', NULL, '2021-03-09', '2021-03-10', '2021-02-23 11:19:01', 0, 3, NULL, NULL);
