INSERT INTO `crm_request_manage` VALUES (1224, 2021, 3, '研发', 'pc java', 1413, '黄磊', 'pc老师端-增加学情分析', 'pc老师端-增加学情分析：\n主流程：1.老师每上几节课就需要填写该阶段的学情分析--2.填写之后，在crm-学生主页-学情分析下面生成一条记录，并可以复制h5链接\n规则：\n新生：第一阶段是单科上4次课，老师需要填写第一阶段学情分析，之后是每6次课填写一次（老生：都是每6次课填写一次）\n具体见原型：https://axhub.im/pro/bee302d728f0c603\n', 2, '2021-02-28', '2021-03-05', '2021-03-15', '2021-03-18', '2021-02-26 10:26:52', 1, 3, NULL, NULL);
