INSERT INTO `crm_request_manage` VALUES (1252, 2021, 3, '研发中心', 'crm', 1945, '黄亦榕', '财务-业绩目标-优化', '个人业绩中：\n1. 不要展示代理小组的数据。\n2. 只展示顾问和学管的小组数据。\n3. 不要展示大区的数据，只展示大区的小组的数据。例如：新签大区，下面有(巅峰队，雷神队，xxx) 只展示下面的三个队伍的数据。', 2, '2021-03-04', NULL, '2021-03-04', '2021-03-04', '2021-03-04 09:48:22', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1253, 2021, 3, '教学中心', 'crm', 1413, '黄磊', '自动匹配配置新增教资参数', 'crm-品控教务-自动匹配配置：\n1.新增、修改匹配规则的时候（包括任何类型的试听推送、包括全职和兼职），增加教师资格证：有、不限，设置有教资的话推送的老师就是全部必须都要有教师资格证，设置不限的话，就是不管有没有教资都可以\n2.改动地方：精准规则、补推规则、二次补推规则\n3.原型见附件', 2, '2021-03-04', NULL, '2021-03-12', '2021-03-18', '2021-03-04 10:29:27', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1254, 2021, 3, '业务中心', 'Java后台', 1945, '黄亦榕', '企微--辅导君工作通知', '每4/6次课后老师需填写学情分析表，填写完成后企微【消息中心】会通知该学生的持有人查看并发送学情分析链接给学生家长。', 2, '2021-03-04', NULL, '2021-03-15', '2021-03-17', '2021-03-04 10:51:10', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1255, 2021, 3, '教学中心', 'crm', 1413, '黄磊', '全职和兼职的试听都要做精准推送', '原需求：匹配规则中的必须匹配---推送的时候,检查试听课时间 ，小学、初中不做正式课时间和试听课时间冲突判断，所有时间都推送，高中学段时间冲突则不推送\n改之后：所有学段--小初高都要做正式课时间和试听课时间冲突判断，如果冲突则不要推送给老师（包括全职和兼职）', 2, '2021-03-04', NULL, '2021-03-10', '2021-03-11', '2021-03-04 11:02:00', 1, 3, NULL, NULL);
