INSERT INTO `crm_request_manage` VALUES (1265, 2021, 3, '业务中心', '辅导君CRM', 1945, '黄亦榕', '解绑后再次绑定该老师', '一、需求背景\n目前系统中学生超过120天未上课系统会自动解除师生绑定关系，学生复课时候想要绑定该老师，但该老师绑定学生已超过15个，则只有两种途径可以重新绑定：1、顾问重新发起试听后取消，再绑定该老师；2、通过教务申请由教务审批。\n二、需求描述\n优化重新绑定规则，系统自动解绑的学生想再次绑定原老师，但该老师带生已超过15人，顾问可以立即重新绑定，无需发起试听或通过教务审批。', 0, '2021-03-15', NULL, '2021-03-24', '2021-03-25', '2021-03-05 10:26:35', 0, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1266, 2021, 3, '师资', '老师app', 1413, '黄磊', '老师app在认证的过程中可以查看教师规范', '老师app，老师在整个认证环节时，只要不是全职，就可以查看我的-教师规范这个版块，以下是具体情况：\n1.只是注册，未申请，全职和兼职都可以查看\n2.申请了老师，全职不能查看这个板块，兼职的话可以查看\n3.认证成功过后，全职则不显示教师规范，兼职显示', 2, '2021-03-05', NULL, '2021-03-10', '2021-03-11', '2021-03-05 10:32:12', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1267, 2021, 3, '师资', '老师app', 1413, '黄磊', '老师app-个人介绍做字数下限', '老师个人介绍，做字数下限，最少输入200字。不满足要求时，不允许提交成功，并进行提示：最少输入200字\n改动位置：\n1.老师认证时填写基础信息-个人介绍（增加提示：最少输入200字，见附件）\n2.老师app-我的信息-基础信息-个人介绍（增加提示：最少输入200字，见附件）', 2, '2021-03-05', NULL, '2021-03-10', '2021-03-11', '2021-03-05 10:39:25', 1, 3, NULL, NULL);
