INSERT INTO `crm_request_manage` VALUES (1270, 2021, 3, '研发中心', '辅导君CRM ', 1945, '黄亦榕', '学生主页--课程表优化', '部分顾问以及学管反映，目前存在时常不能从学生主页课程表点击【具体排课时间】进入【课程详情页】的现象，此问题发生频率较高，影响用户体验，需优进一步优化。', 2, '2021-03-05', NULL, '2021-03-10', '2021-03-11', '2021-03-05 15:06:45', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1271, 2021, 5, '业务中心', '辅导君CRM', 1945, '黄亦榕', '学生主页--课程表新增【预测表】下载功能', '1、目前顾问每月初会用excel对持有学生进行当月课程预测排课，排好课后再将课程表发送给家长，\n为进一步提高顾问工作效率，系统支持生成预排课并下载的功能；\n2、具体需求详见PRD。', 2, '2021-04-05', '2021-04-07', '2021-05-10', '2021-05-13', '2021-03-05 16:23:15', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1272, 2021, 3, '市场部', 'Java项目', 1865, '蒋明', 'crm转介绍渠道管理', '一、需求描述: \n转介绍渠道管理中数据统计的顾问小组和大区，没有同步更新现在最新的组织架构，为了统计活动数据情况和给顾问发放激励，需要同步更新最新组织架构\n二、功能规划：\n下载统计时，需同步顾问的最新组织架构信息。', 0, '2021-03-05', NULL, '2021-03-10', '2021-03-11', '2021-03-05 16:52:44', 0, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1273, 2021, 3, '研发部', '老师app', 1413, '黄磊', '老师app认证流程中增加设置标签', '老师app-在认证老师的流程中，签约步骤后面增加：设置标签的这一步（具体见附件原型）：\n1.全职和兼职都增加这一步骤\n2.必须设置，强制项，不设置无法进入下一步\n3.教材版本、课程类型、教学内容、教学特点与教龄，每一项必须最少设置一个标签，不满足要求时，确定按钮置灰不让提交，并进行提示：每项至少设置一个', 2, '2021-03-05', NULL, '2021-03-16', '2021-03-17', '2021-03-05 17:15:32', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1274, 2021, 3, '研发部', '辅导君CRM', 1865, '蒋明', 'CRM修改系统账号日志完善', '功能描述：\n若CRM系统账号信息及权限被修改，则需要记录下修改人及修改时间的操作日志内容。\n记录操作请求日志。', 2, '2021-03-05', NULL, '2021-03-10', '2021-03-11', '2021-03-05 17:51:51', 1, 3, NULL, NULL);
