INSERT INTO `crm_request_manage` VALUES (1276, 2021, 3, '研发部', 'crm', 1413, '黄磊', 'crm上老师跟学生绑定和解绑的时候需要调接口通知java  ', '为配合PC客户端学情分析的任务--APPM-1825，crm上老师跟学生绑定和解绑的时候需要调接口通知java  ', 2, '2021-03-08', NULL, '2021-03-15', '2021-03-17', '2021-03-08 13:59:40', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1277, 2021, 3, '研发部', '辅导君CRM', 1865, '蒋明', '校宝聚合支付-转介绍关联', '一、需求背景：\n目前已知使用校宝聚合支付快捷收款的订单，转介绍逻辑没有处理，之前需求未考虑到转介绍相关订单的逻辑，因此需要补充上。\n二、功能规划：\n1、校宝聚合支付收款成功功能，若是转介绍订单，则需同步转介绍相关奖励信息。\n2、待考虑转介绍升级vip、赠送课时等。', 2, '2021-03-08', NULL, '2021-03-10', '2021-03-11', '2021-03-08 14:20:02', 1, 3, NULL, NULL);
