INSERT INTO `crm_request_manage` VALUES (1473, 2021, 7, 'BI平台', 'BI', 1952, '杨浩', '顾问数据画像', '可以进行从多个层次维度展示顾问的数据，形成数据画像。\n比如：顾问拥有线索情况、顾问对外电话情况、顾问企微沟通情况、顾问成单信息等。\n数据通过分层模式，可以在多个顾问数据之间进行对比或者单个顾问的详情信息。', 2, '2021-06-28', '2021-06-28', '2021-07-08', '2021-07-09', '2021-05-07 10:32:33', 1, 3, 2, NULL);
INSERT INTO `crm_request_manage` VALUES (1474, 2021, 7, '研发中心', '辅导君crm  H5开发', 1945, '黄亦榕', '我的学生列表', '一、现状\n1、当前我的学生列表展示项过多且冗杂，顾问不能快速筛查到需要的学生信息；\n2、信息列行距过高，一屏展示的信息项有限；\n二、需求描述\n1、增加可编辑备注以及置顶等功能项\n2、增加操作栏\n3、增加打标签功能\n4、可个性化编辑列表展示项', 2, '2021-05-20', '2021-06-28', '2021-06-25', '2021-07-01', '2021-05-07 10:54:48', 1, 3, 3, NULL);
