INSERT INTO `crm_request_manage` VALUES (1548, 2021, 8, '教学中心', 'crm', 1413, '黄磊', '顾问发起试听做限制', '背景：有顾问1个学生发起5次试听，选无人接单的走手动审批，就为了不想用自动匹配的老师。一般无人接单都会批复\n需求: 同一个学生，同一个学科，发起了一个试听之后，不能多次发起，必须等前一个试听结束或者取消之后才能继续发起，不予许重复多次发\n再次发起时，在发起试听页面选择科目后进行提示：“您有一个该科目的试听，请结束试听或取消后才能再次发起”\n', 0, '2021-05-18', NULL, '2021-07-14', '2021-07-15', '2021-05-18 09:36:59', 0, 2, 2, NULL);
INSERT INTO `crm_request_manage` VALUES (1549, 2021, 6, '新签大区', '辅导君CRM', 2337, '陈青杨', '顾问【我的联系人】成单前列表优化', '一、需求背景: \n方便顾问和主管经理更好的判断未成交客户的渠道来源以及数据分析\n\n二、功能规划：\n1、顾问【我的联系人】跟进客户、试听客户菜单\n1）搜索栏\n增加：年级、线索来源筛选\n\n2、顾问【我的联系人】待成单菜单\n1）搜索栏\n增加：线索来源筛选\n2）列表\n增加：线索来源字段\n\n三、产品原型\n详见jira任务', 2, '2021-06-11', NULL, '2021-06-17', '2021-06-17', '2021-05-18 14:07:10', 1, 3, NULL, NULL);
