INSERT INTO `crm_request_manage` VALUES (1572, 2021, 7, '家学堂', 'CRM', 1945, '黄亦榕', '家长问卷调查（待补充）', '关于家长问卷的功能：（主要针对家长回复的问题，业务人员进行主动回访）\n需求：做成后端可配置。如何实现配置之后的推广、数据回收、顾问回访等流程。\n需要考虑新线索、已存在客户家长两种类型的客户信息回收，如何整合给业务人员去主动沟通。', 0, '2021-06-14', NULL, '2021-06-22', '2021-06-30', '2021-05-24 17:24:05', 0, 3, 2, NULL);
