INSERT INTO `crm_request_manage` VALUES (1650, 2021, 7, '家学堂', 'CRM', 1945, '黄亦榕', '家学堂项目推广监测', '目的：促进顾问主动引流\n方向：顾问通过什么途径发送给家长（比如问卷调查、广告链接）、发送之后家长有没有看（家长轨迹）、或者有些需要家长填写的信息顾问怎么能直观的获取到从而主动沟通家长（后台监控能够监控到顾问行为轨迹）\n可以兼容问卷调查的整个逻辑。', 0, '2021-07-01', NULL, '2021-06-30', '2021-06-30', '2021-06-09 10:04:17', 0, 2, 2, NULL);
INSERT INTO `crm_request_manage` VALUES (1651, 2021, 7, '客服', 'CRM', 1413, '黄磊', '客服-客服满意度管理：多级配置', '背景：crm客服满意度配置中，新增一个下拉框：问题类型，内容填写：学生端问题、教学端问题、业务端原因、教学模式问题、系统端问题；学生主页回访填写时选择不同问题类型，展示出不同具体问题描述（问题描述内容以excel形式放在附件图中）\n需求：在客服-客服满意度管理中，需要满足背景中描述的至少父级+子级的配置模式。前端表现为根据父级选择的不同，展示出不同的子级。', 1, '2021-06-17', NULL, '2021-07-07', '2021-07-08', '2021-06-09 10:30:24', 0, 2, 1, NULL);
INSERT INTO `crm_request_manage` VALUES (1652, 2021, 6, 'sop', 'H5开发', 2337, '陈青杨', '立即签单时增加收款制度弹窗', '一、需求背景: \n方便顾问在发起签单时简略的浏览公司收款制度，让顾问在知悉公司的公司收款制度的前提下完成签单。\n\n二、功能规划：\n1.顾问在crm系统发起签单的时候，增加公司首款制度弹窗。\n2.顾问在浏览公司首款制度时，强制浏览5s后才能点击确认。\n3.顾问点击已经知晓后点击确认按钮，完成后，进入签单页面。\n4.顾问在点击公司首款制度PDF时能下载观看。\n\n三、产品原型\nhttps://v6.modao.cc/app/87a9f476e8f3ae508504079ed227ac7d0b71e0a6?simulator_type=outside_artboard&sticky', 2, '2021-06-10', NULL, '2021-06-15', '2021-06-16', '2021-06-09 10:50:45', 1, 3, NULL, NULL);
