INSERT INTO `crm_request_manage` VALUES (1670, 2021, 6, '研发', 'crm', 1413, '黄磊', '学生APP注销接口', '因app注销合规问题,需要提供注销接口(具体页面见附件)\n注销规则(在学生app退出登录页面增加了注销账号按钮):\n1.手机号刚注册，线索未分配：点击注销按钮返回到首页，退出登录状态，后端需要把该手机号 为未注册状态，该手机号之后可以重新注册\n2.手机号刚注册，线索已分配：点击注销按钮出现弹窗提示:\"注销账号请拨打客服电话：400-036-2990或者添加客服微信：15355450035进行注销\"\n3.手机号已经是客户：点击注销按钮出现弹窗提示:\"注销账号请拨打客服电话：400-036-2990或者添加客服微信：15355450035进行注销\"\n\n', 2, '2021-06-13', NULL, '2021-06-13', '2021-06-13', '2021-06-13 11:03:58', 1, 2, NULL, NULL);
