INSERT INTO `crm_request_manage` VALUES (1725, 2021, 7, '运营部', 'CRM项目', 2337, '陈青杨', '转介绍数据拉取', '数据拉取维度\n①介绍人学生id\n②被介绍人学生id\n③被介绍人线索创建时间：被介绍人在crm中作为线索的创建时间\n④是否试听：被介绍人是否完成了试听\n⑤是否签单：被介绍人是否完成了签单\n⑥签单金额：被介绍人新签的订单实付金额（不含续费，含补差价新签）', 2, '2021-06-30', NULL, '2021-07-01', '2021-07-02', '2021-06-30 15:00:33', 1, 2, 1, NULL);
