INSERT INTO `crm_request_manage` VALUES (1739, 2021, 8, '运营部', 'Java后台 H5开发', 2337, '陈青杨', '分享有礼工具化', '一、需求描述：为了能精准的获取家长线索，现对分享有礼进行工具化操作。\n\n二、功能规划：\n1.顾问在crm后台活动运营-分享海报（顾问）获取海报和链接.\n2.通过企业微信发送给家长\n3.家长A点击链接或者扫描二维码跳转到分享有礼H5页面可以生成带着小程序二维码的活动页面\n4.家长B扫描小程序的二维码进入领取课时的活动页面获取微信手机号，根据家长填写的信息生成线索。\n\n三、产品原型\n  见jira附件', 2, '2021-07-14', '2021-07-16', '2021-08-04', '2021-08-05', '2021-07-06 14:11:49', 1, 3, 2, 1620);
