INSERT INTO `crm_request_manage` VALUES (1780, 2021, 7, '研发', 'pc客户端', 1413, '黄磊', '页面时长统计', '需要统计老师在一节课中，这节课在所有的课件中 停留时长最长的那一页（退出教室后不再计算，默认的空白课件也要计算，如果是0秒，就只显示分）：\na.显示：上课后多少分多少秒开始停留的，停留的时长是多少\nb.如果一个页面停留了多次，就取停留时间最长的那一次，不要叠加\nc.上课后才开始计算，时间跑完后不再统计', 2, '2021-07-20', NULL, '2021-07-26', '2021-07-29', '2021-07-20 11:46:00', 1, 3, 3, 1778);
