INSERT INTO `crm_request_manage` VALUES (1795, 2021, 8, '研发', 'java', 1413, '黄磊', 'crm-工单下面增加【服务器实时状态】', 'crm-工单下面增加【服务器实时状态】，具体见原型：\n1.展示四台服务器的房间情况，不同状态的房间 用不同的色块展示（展示所有课程类型）；\n2.每个房间显示：上课声道以及老师和学生是否用了新协议；\n3.展示的数据为每天的实时数据。\n\n\n', 2, '2021-08-05', '2021-08-06', '2021-08-11', '2021-08-12', '2021-07-22 16:37:56', 1, 3, 2, NULL);
