INSERT INTO `crm_request_manage` VALUES (1805, 2021, 10, '师资', '老师app  java  h5 crm', 1413, '黄磊', '老师app增加上传证书功能', '老师app-我的界面增加【我的证书】的功能，可以选择上传证书（可以上传多张图片）：\n1.crm-老师菜单下面增加【老师证书审核】，可以对老师上传的每张图片进行审核，审核通过后的证书显示在crm老师个人主页和老师app\n2.java只提供老师app 的接口，crm审核以及其他接口由crm提供\n3.具体规则见原型', 0, '2021-08-06', '2021-08-10', '2021-09-09', '2021-09-09', '2021-07-28 16:52:42', 0, 3, 3, NULL);
