INSERT INTO `crm_request_manage` VALUES (1816, 2021, 8, '研发中心', '辅导君CRM', 1945, '黄亦榕', 'sop任务优化', '一、需求背景：\n政策要求，发给家长的信息中不允许出现布置作业的字样\n二、需求描述：\nsop任务问题（包括首次课回访、20%可好回访、50%课耗回访、80%课耗回访、4/6次课学情分析回访）均删除包含【家庭作业/课后作业】的问题选项。', 2, '2021-08-05', NULL, '2021-08-05', '2021-08-05', '2021-08-05 11:13:10', 1, 3, 2, NULL);
