INSERT INTO `crm_request_manage` VALUES (1818, 2021, 8, '研发中心', '辅导君CRM', 1945, '黄亦榕', '学生主页', '一、操作人：销售管理员\n二、需求描述：学生已升至大学年级且有课时的情况下，业务管理员可在学生主页通过点击【毕业客户】按钮将该学生移至大学池，若学生年级为大学年级且无课时，业务管理员点击【毕业客户】按钮，该学生流转至毕业池\n三、判断标准：学生年级+课时', 2, '2021-08-05', NULL, '2021-08-11', '2021-08-12', '2021-08-05 13:51:28', 1, 3, 2, NULL);
INSERT INTO `crm_request_manage` VALUES (1819, 2021, 8, '研发', 'h5客户端', 1413, '黄磊', '白板连接优化', '原规则：下课后，学生没有退出教室白板会一直连着造成服务器压力\n改为： 下课后白板断开连接，以及优化断线重连功能', 2, '2021-08-05', NULL, '2021-08-05', '2021-08-05', '2021-08-05 15:57:38', 1, 3, 2, NULL);
INSERT INTO `crm_request_manage` VALUES (1820, 2021, 8, '研发', 'pc客户端', 1413, '黄磊', 'PC客户端优化-OSS上传错误码解析优化', 'PC客户端优化-OSS上传错误码解析优化\n内容如下：\n网络异常情况-RequestError：提示“网络异常，请检查网络连接”\n网络超时情况-SocketAssignTimeoutError：提示“数据已传送到火星，请检查网络情况”\n本地直接与标准时间不同步-RequestTimeTooSkewedError：提示“您电脑当前时间与标准时间不同步，请更改时间”\n其他情况根据使用场景提示上传失败文案\n\n更改上传功能有：\n1 教室内上传课件\n2 我的课件-上传课件\n3 我的课件-课件制作-导课件\n4 课件制作-上传图片、上传视频、添加题库题目、添加试卷题目、保存\n5 申请精品课件-添加作业\n6 错题列表-错题分析-生成错题报告\n7 作业-提交作业-上传图片\n8 刷题错题-上传图片', 2, '2021-08-05', NULL, '2021-08-05', '2021-08-05', '2021-08-05 16:00:01', 1, 3, 2, NULL);
INSERT INTO `crm_request_manage` VALUES (1821, 2021, 8, '研发中心', '辅导君CRM+H5', 1945, '黄亦榕', '我的联系人列表', '一、需求描述：\n1、列表搜索栏新增家学堂客户搜索项\n2、列表字段新增家学堂客户\n3、添加位置：我的线索、跟进客户、试听客户、待成单客户这四个页面\n', 0, '2021-08-05', NULL, '2021-08-11', '2021-08-12', '2021-08-05 18:29:02', 0, 3, 2, NULL);
