INSERT INTO `crm_request_manage` VALUES (1827, 2021, 8, '市场', 'java  学生app', 1413, '黄磊', '合规-学生app课时显示修改', '学生app课时显示修改，\n1.前提：\n根据学生的学段（小学  初中  高中 大学、其他）来判断是什么规则：1节课=30min /1节课=40min\n小初：1节课=30min   \n高中、大学、其他： 1节课=40min \n\n2.具体需要修改的地方（具体页面见附件截图）\n我的页面：\na.剩余小时：xxx（x节课）以及点击剩余小时的页面：已用节数、剩余节数\nb.感叹号提示文案：\n小初：“亲爱的家长，1课时=30分钟”\n高中、大学、其他：“亲爱的家长，1课时=40分钟”\nc.课时流水页面：剩余节数、锁定节数、已上节数以及列表\nd.我的订单页面：待支付、已支付、已取消的每条订单记录需要加上说明文案（具体见附件，视频版也要改）\n注：d点后台、移动端都要改，其他的只需要后台修改即可\n\n', 2, '2021-08-06', NULL, '2021-08-16', '2021-08-18', '2021-08-06 10:46:31', 1, 3, 3, NULL);
