INSERT INTO `crm_request_pool` VALUES (40, '教学中心', 839, '潘丰', '续费规划', '续费规划，顾问端根据业务节奏，可以点击某学员，则该学员绑定的所有老师将收到续费规划的填写推送任务。要求老师在3日内填写完成。模块由教学中心设计（跟试听反馈相似，由文字编辑的部分，也有知识点勾选的部分）。但同一学员2个月之内顾问不得重复点击填写。', NULL, NULL, NULL, '2020-07-01', '2020-06-19 15:00:10', 2, NULL, NULL, NULL, NULL, '2020-07-01 15:19:29', 1413, '黄磊', NULL, 341);
INSERT INTO `crm_request_pool` VALUES (41, '教学中心', 839, '潘丰', '扩更代试听及直接绑定', '扩科试听和直接绑定要求只能启用0-1的持证新老师或全职。更换或代课试听和直接绑定只能启用“普通老师”“教坛新秀”两个级别的持证兼职或全职。\n\n若申请启用非持证兼职或高级别持证兼职，走特殊匹配邮件，规则跟之前一样。', NULL, NULL, NULL, '2020-06-30', '2020-06-19 15:02:37', 3, NULL, NULL, NULL, NULL, '2020-06-19 17:32:07', 1204, '杨苗苗', NULL, 292);
