INSERT INTO `crm_request_pool` VALUES (46, '市场部', 504, '关百成', '小程序&服务号矩阵开发', '\n开发目的：实现服务号关联小程序的引流路径\n现阶段需求：通过将课程评价小程序上的用户，导流至微信服务号，沉淀用户的同时引导用户参与转介绍裂变活动来实现用户增长\n\n矩阵形式：用户通过课程评价小程序（试听&正式课）页面，跳转并关注服务号或用户通过扫码搜索直接关注，然后参与服务号上裂变活动（领取学习资料、抽奖、分享得课时等）\n\n开发内容：小程序聚合单页面（7月15日上线）、学习资料库小程序（8月上线）、小程序抽奖页（7月30日上线）\n详细内容见文档', NULL, NULL, NULL, '2020-07-31', '2020-06-19 15:22:19', 0, '该任务已变更，分为小程序展示公众号弹窗、小程序引导跳转、小程序客服及资料包活动等', '2020-07-27 10:45:12', 453, '毛家俊', NULL, NULL, NULL, NULL, NULL);
