INSERT INTO `crm_request_pool` VALUES (60, '教学中心', 839, '潘丰', 'PC端授课', '来自全职老师杨建萍希望老师端可以双开，也就是同时打开两个操作界面。方便处理一些应急情况。\n比如：孩子家长突然要求发课件打印后上。老师端不能双开，需要所有课下课后才能下载课件发给家长，时间间隔太少家长来不及出去打印。\n比如：孩子当天突然临时调整上课内容，老师端可以双开就能处理突发状况，上传新的课件，不影响课程安排。\n比如：孩子家长临时要求，再给多布置一份作业，老师端可以双开立马布置。当天全部下课后再布置的话，孩子没有时间做额外的作业了。', NULL, NULL, NULL, '2020-07-31', '2020-07-07 19:36:38', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
