INSERT INTO `crm_request_pool` VALUES (153, '教学中心', 839, '潘丰', '手动批复经理审批', '1 经理审批新增：发起匹配后如果有老师申请，请显示申请老师的姓名 ID，品控等级，学生数。\n2 申请原因选项那里：特殊指定和特殊要求看起来有些类似，顾问经理反馈顾问不理解。把特殊指定改成：家长指定老师（有截图），家长特殊要求改为：申请老师无法满足家长要求。“其他”选项取消，所有标签都要写文字必填理由', NULL, NULL, NULL, '2020-11-19', '2020-11-17 09:36:53', 0, '已经开发完成', '2020-12-01 09:45:10', 73, '何宇明', NULL, NULL, NULL, NULL, NULL);
