INSERT INTO `crm_request_pool` VALUES (168, '续转中心', 455, '程翔', '老师排课前后10分钟间隔取消', '针对老师端排课系统：取消不同学生中间的10分钟间隔时间，这样老师可以拍更多的学生', '国家规定学生每节课上课不可以超过40分钟，对老师应该没有规定吧？A学生课程结束以后，老师也必须要过10分钟以后才能排课，这个对老师操作影响非常大，前后10分钟就是20分钟，很多排课老师都拍不了', 'A学生下午2点结束，那么B学生就可以从2点开始排课！', NULL, '2020-12-18', '2020-12-16 15:21:28', 0, '不同学生直接排课没有 10分钟休息的限制。', '2020-12-23 13:51:07', 73, '何宇明', NULL, NULL, NULL, '2020-12-16 15:21:57', NULL);
