INSERT INTO `crm_request_pool` VALUES (206, '教学中心', 839, '潘丰', '自动匹配规则配置新增教资参数', '因为持证代课率最近听说风声收紧，近2个月持证代课率指标下滑\n所以，CRM--品控教务--自动匹配规则配置里，新增是否有教资参数，我可以根据库存老师体量和试听需求等自主编辑调节', NULL, NULL, NULL, '2021-02-18', '2021-02-10 14:34:29', 0, '已排期', '2021-03-08 14:23:21', 1413, '黄磊', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `crm_request_pool` VALUES (207, '市场中心', 242, '赖佳伟', '客户详情页修改转介绍功能优化', '市场部手动修改渠道来源后，若数据属于转介绍来源，允许添加介绍人信息。', '当前顾问通过邮件申请判定转介绍关系，市场中心根据凭证判定存在转介绍关系后，会手动将来源修改为：手动录入转介绍。 但是在修改转介绍人信息时，提交ID后，系统提示：当前用户不是转介绍用户。导致顾问无法后台提交转介绍申请，得通过审批邮件作为凭证，走其他增课申请，人工审核通过。', '市场部可以手动修改渠道来源，若属于转介绍渠道，则可以添加介绍人信息。', NULL, '2021-02-28', '2021-02-20 10:17:20', 0, '已与唯佳沟通，废弃', '2021-03-17 17:33:28', 1865, '蒋明', NULL, NULL, NULL, '2021-02-20 10:18:37', NULL);
INSERT INTO `crm_request_pool` VALUES (208, '师资运营', 2018, '胡冬', 'crm', '1. crm---老师---注册老师界面，添加来源筛选项H5和APP，可以通过不同来源看到注册量，还可以在这里看到每个渠道注册之后有多少未下载APP的\n2. CRM---老师代理商---H5数据管理页面，下载数据之后保留网页上的“老师状态”一栏', NULL, '能够得到指定时间段内不同来源的注册量数据\n参照老师代理商---H5数据管理', 'https://fudaojun-img-2.oss-cn-hangzhou.aliyuncs.com/crm-image/demand-pool/49e2393ada144f5ebf00a8f2aef03bf9.rar', '2021-03-19', '2021-02-20 14:12:36', 0, '已单独排期', '2021-03-24 09:25:44', 1413, '黄磊', NULL, NULL, NULL, '2021-03-18 13:48:33', NULL);
INSERT INTO `crm_request_pool` VALUES (209, '投放部', 242, '赖佳伟', 'CRM-渠道数据撞库去重功能', 'CRM新增功能，可以实现渠道提供号码，与我司crm进行撞库、去重。去重后的号码反馈给渠道，再进行电话、社群邀约，节省人力成本。', '当前TMK、社群类渠道数据重复率较高，故渠道要求提供数据源，先与我们现有的CRM数据进行撞库、去重后再进行邀约，供应。节省人力成本', '渠道数据库与我司撞库，去重。', NULL, '2021-02-28', '2021-02-22 17:21:48', 3, NULL, NULL, NULL, NULL, '2021-03-03 10:12:05', 1865, '蒋明', NULL, 1244);
INSERT INTO `crm_request_pool` VALUES (210, '市场部', 504, '关百成', '服务号—分享有礼白名单', '存在问题：由于分享有礼活动升级后面，之前课时转移的用户无法参与\n解决方案：经过和产品沟通后，开发白名单功能，由技术端拉取课时转移用户的数据，并将其批量添加至白名单中，让其可以正常参与活动', NULL, NULL, NULL, '2021-03-05', '2021-02-23 16:21:51', 0, '功能已上线', '2021-03-03 10:11:05', 1865, '蒋明', NULL, NULL, NULL, '2021-02-23 18:09:00', NULL);
